const body = $input.item.json.body || $input.item.json;
const files = body.files || [];
const namespace = body.namespace || 'default';

if (!Array.isArray(files) || files.length === 0) {
  throw new Error('files array is required. Format: {"files": [{"fileName": "test.txt", "text": "content..."}], "namespace": "default"}');
}

return files.map(file => ({
  json: {
    fileName: file.fileName || 'unknown.txt',
    text: file.text || file.content || '',
    source: (file.fileName || 'unknown').replace('.txt', ''),
    namespace: namespace
  }
}));

const inputItem = $input.item;
const inputData = inputItem.json || {};

const text = inputData.text;
const source = inputData.source || inputData.fileName || 'unknown';
const namespace = inputData.namespace || 'default';

if (!text || typeof text !== 'string') {
  throw new Error(`Text field is required. File: ${source}`);
}

const chunkSize = 600;
const overlap = 100;
const chunks = [];
let index = 0;

for (let i = 0; i < text.length; i += chunkSize - overlap) {
  const chunk = text.slice(i, i + chunkSize);
  if (chunk.trim().length > 0) {
    chunks.push({
      id: `chunk_${source}_${Date.now()}_${index}`,
      text: chunk,
      chunk_index: index,
      source: source
    });
    index++;
  }
}

return [{
  json: {
    chunks: chunks,
    namespace: namespace,
    source: source
  }
}];

const chunks = $input.item.json.chunks;
const texts = chunks.map(chunk => chunk.text);

return [{
  json: {
    texts: texts,
    chunks: chunks
  }
}];

const embeddingsResponse = $input.item.json;
const chunks = $('Prepare Batch').item.json.chunks;
const namespace = $('Chunk Text').item.json.namespace;

if (!embeddingsResponse.data || !Array.isArray(embeddingsResponse.data)) {
  throw new Error('Invalid embeddings response');
}

const vectors = embeddingsResponse.data.map((item, index) => ({
  id: chunks[index].id,
  values: item.embedding,
  metadata: {
    text: chunks[index].text,
    source: chunks[index].source,
    chunk_index: chunks[index].chunk_index
  }
}));

return [{
  json: {
    vectors: vectors,
    namespace: namespace,
    vectorsCount: vectors.length
  }
}];

const allUpsertResponses = $input.all();
let totalUpserted = 0;
const fileResults = [];

allUpsertResponses.forEach((item, index) => {
  const response = item.json;
  const upsertedCount = response.upsertedCount || 0;

  totalUpserted += upsertedCount;

  fileResults.push({
    fileIndex: index + 1,
    upserted: upsertedCount
  });
});

const filesProcessed = $('Parse Input').all().length;

return [{
  json: {
    status: 'success',
    totalFiles: filesProcessed,
    totalUpserted: totalUpserted,
    fileResults: fileResults
  }
}];
